import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Paper,
  Box,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Grid,
  Card,
  CardContent,
  CardMedia,
} from "@mui/material";
import TextField from "@mui/material/TextField";
import AppBar1 from "./Appbar";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../AuthContext";

const paperStyle = {
  padding: "50px 20px",
  width: 600,
  margin: "20px auto",
};

export default function AdminP() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const role = "ADMIN";
  const [error, setError] = useState("");
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();
  const { logout } = useAuth();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const getUsers = async () => {
      const data = await fetchUsers();
      setUsers(data);
    };

    getUsers();
  }, []);

  useEffect(() => {
    const getProducts = async () => {
      const data = await fetchProducts();
      setProducts(data);
    };

    getProducts();
  }, []);

  const handleLogout = async () => {
    const accessToken = localStorage.getItem("access_token");
    const response = await fetch("http://localhost:8080/logout", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${accessToken}`,
      },
    });

    if (response.ok) {
      localStorage.removeItem("access_token");
      localStorage.removeItem("refresh_token");
      logout();
      console.log("Logout successful");
      navigate("/adminLogin");
    } else {
      console.log("Logout failed");
    }
  };

  const handleClick = (e) => {
    e.preventDefault();
    if (!firstName || !lastName || !username || !password) {
      setError("Please fill out all fields.");
      return;
    }

    const infos = { firstName, lastName, username, password, role };
    console.log(infos);

    fetch("http://localhost:8080/register", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(infos),
    })
      .then((response) => {
        if (response.ok) {
          navigate("/login");
        } else {
          return response.json().then((data) => {
            console.log("Response data:", data);
            setError(data.message || "Registration failed. Please try again.");
          });
        }
      })
      .catch((error) => {
        setError("An error occurred. Please try again.");
        console.error("Error:", error);
      });
  };

  const fetchUsers = async () => {
    try {
      const accessToken = localStorage.getItem("access_token");
      const response = await fetch("http://localhost:8080/users", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Error fetching users:", error);
      return [];
    }
  };

  const handleUserUpdate = async (user) => {
    const accessToken = localStorage.getItem("access_token");

    const updatedUser = {
      firstName: prompt("Enter new first name", user.firstName),
      lastName: prompt("Enter new last name", user.lastName),
      username: prompt("Enter new username", user.username),
      role: user.role,
    };

    try {
      const response = await fetch(`http://localhost:8080/users/${user.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${accessToken}`,
        },
        body: JSON.stringify(updatedUser),
      });

      if (response.ok) {
        setUsers((prevUsers) =>
          prevUsers.map((u) =>
            u.id === user.id ? { ...u, ...updatedUser } : u
          )
        );
        console.log("User updated successfully");
      } else {
        console.error("Failed to update user");
      }
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  const handleUserDelete = async (user) => {
    const accessToken = localStorage.getItem("access_token");

    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${user.firstName} ${user.lastName}?`
    );

    if (!confirmDelete) {
      return;
    }

    try {
      const response = await fetch(`http://localhost:8080/users/${user.id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (response.ok) {
        setUsers((prevUsers) => prevUsers.filter((u) => u.id !== user.id));
        console.log("User deleted successfully");
      } else {
        console.error("Failed to delete user");
      }
    } catch (error) {
      console.error("Error deleting user:", error);
    }
  };

  const fetchProducts = async () => {
    try {
      const accessToken = localStorage.getItem("access_token");
      const response = await fetch("http://localhost:8080/products", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      return data;
    } catch (error) {
      console.error("Error fetching products:", error);
      return [];
    }
  };

  const handleProductUpdate = async (product) => {
    const accessToken = localStorage.getItem("access_token");

    const updatedProduct = {
      productName: prompt("Enter new product name", product.productName),
      productDescription: prompt(
        "Enter new product description",
        product.productDescription
      ),
      productPrice: prompt("Enter new product price", product.productPrice),
      productImage: prompt("Enter new product image URL", product.productImage),
    };

    try {
      const response = await fetch(
        `http://localhost:8080/products/${product.productId}`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${accessToken}`,
          },
          body: JSON.stringify(updatedProduct),
        }
      );

      if (response.ok) {
        setProducts((prevProducts) =>
          prevProducts.map((p) =>
            p.productId === product.productId ? { ...p, ...updatedProduct } : p
          )
        );
        console.log("Product updated successfully");
      } else {
        console.error("Failed to update product");
      }
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  const handleProductDelete = async (product) => {
    const accessToken = localStorage.getItem("access_token");

    const confirmDelete = window.confirm(
      `Are you sure you want to delete ${product.productName}?`
    );

    if (!confirmDelete) {
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:8080/products/${product.productId}`,
        {
          method: "DELETE",
          headers: {
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      if (response.ok) {
        setProducts((prevProducts) =>
          prevProducts.filter((p) => p.productId !== product.productId)
        );
        console.log("Product deleted successfully");
      } else {
        console.error("Failed to delete product");
      }
    } catch (error) {
      console.error("Error deleting product:", error);
    }
  };

  return (
    <div>
      <AppBar1 />
      <Container maxWidth="md">
        <Box mt={4}>
          <Paper elevation={3}>
            <Box p={4}>
              <Typography variant="h4" gutterBottom>
                Admin Panel
              </Typography>
              <Typography variant="body1">
                Welcome to the admin dashboard. Here you can manage users,
                settings, and more.
              </Typography>
              <Button
                variant="contained"
                color="primary"
                onClick={handleLogout}
              >
                Logout
              </Button>
            </Box>
          </Paper>
        </Box>

        <Paper elevation={3} style={paperStyle}>
          <h1 style={{ color: "#1976d2" }}>Admin Register</h1>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <Box
            component="form"
            sx={{
              "& > :not(style)": { m: 1 },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField
              id="first-name"
              label="First Name"
              variant="outlined"
              fullWidth
              value={firstName}
              onChange={(e) => setFirstName(e.target.value)}
            />
            <TextField
              id="last-name"
              label="Last Name"
              variant="outlined"
              fullWidth
              value={lastName}
              onChange={(e) => setLastName(e.target.value)}
            />
            <TextField
              id="username"
              label="User Name"
              variant="outlined"
              fullWidth
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <TextField
              id="password"
              label="Password"
              variant="outlined"
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button variant="contained" onClick={handleClick}>
              Register
            </Button>
          </Box>
        </Paper>

        <Paper elevation={3} style={{ padding: "20px", marginTop: "20px" }}>
          <Typography variant="h5" gutterBottom>
            Registered Users
          </Typography>
          <TableContainer component={Paper}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>First Name</TableCell>
                  <TableCell>Last Name</TableCell>
                  <TableCell>Username</TableCell>
                  <TableCell>Role</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {users.map((user, index) => (
                  <TableRow key={index}>
                    <TableCell>{user.firstName}</TableCell>
                    <TableCell>{user.lastName}</TableCell>
                    <TableCell>{user.username}</TableCell>
                    <TableCell>{user.role}</TableCell>
                    <TableCell>
                      <Button
                        variant="contained"
                        color="primary"
                        onClick={() => handleUserUpdate(user)}
                        style={{ marginRight: "10px" }}
                      >
                        Update
                      </Button>
                      <Button
                        variant="contained"
                        color="secondary"
                        onClick={() => handleUserDelete(user)}
                      >
                        Delete
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Paper>
        <Grid container spacing={4}>
          {products.map((product) => (
            <Grid item key={product.productId} xs={12} sm={6} md={4}>
              <Card>
                <CardMedia
                  component="img"
                  height="140"
                  image={product.productImage}
                  alt={product.productName}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {product.productName}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {product.productDescription}
                  </Typography>
                  <Typography variant="body1" color="text.primary">
                    Price: ${product.productPrice}
                  </Typography>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={() => handleProductUpdate(product)}
                    style={{ marginRight: "10px", marginTop: "10px" }}
                  >
                    Update
                  </Button>
                  <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => handleProductDelete(product.productId)}
                    style={{ marginTop: "10px" }}
                  >
                    Delete
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );
}
