import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  IconButton,
  Divider,
} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import AppBarUser from "../components/AppbarUser";

export default function Cart() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const fetchCartItems = async () => {
      const accessToken = localStorage.getItem("access_token");
      const userId = localStorage.getItem("userId");

      if (!accessToken || !userId) {
        console.error("User ID or Access Token is not available");
        return;
      }

      try {
        const response = await fetch(`http://localhost:8080/cart/${userId}`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${accessToken}`,
          },
        });

        if (!response.ok) {
          throw new Error("Failed to fetch cart items");
        }

        const data = await response.json();
        setCartItems(data);
      } catch (error) {
        console.error("Error fetching cart items:", error);
      }
    };

    fetchCartItems();
  }, []);

  const handleRemoveItem = (productId) => {
    // Ürün sepetten kaldırma işlemi burada yapılacak
    console.log("Removing item with Product ID:", productId);
    // Bu işlemden sonra cartItems state'ini güncelleyebilirsiniz.
  };

  return (
    <div>
      <AppBarUser />
      <Container maxWidth="lg" style={{ marginTop: "2rem" }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Your Shopping Cart
        </Typography>

        <Divider style={{ marginBottom: "2rem" }} />

        <Grid container spacing={4}>
          {cartItems.length > 0 ? (
            cartItems.map((item) => (
              <Grid item key={item.productId} xs={12}>
                <Card>
                  <Grid container>
                    <Grid item xs={4}>
                      <CardMedia
                        component="img"
                        height="140"
                        image={item.productImage}
                        alt={item.productName}
                      />
                    </Grid>
                    <Grid item xs={8}>
                      <CardContent>
                        <Typography variant="h5" component="div">
                          {item.productName}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {item.productDescription}
                        </Typography>
                        <Typography variant="body1" color="text.primary">
                          Price: ${item.productPrice}
                        </Typography>
                        <Typography variant="body1" color="text.primary">
                          Quantity: {item.quantity}
                        </Typography>
                        <IconButton
                          edge="end"
                          aria-label="delete"
                          onClick={() => handleRemoveItem(item.productId)}
                        >
                          <DeleteIcon />
                        </IconButton>
                      </CardContent>
                    </Grid>
                  </Grid>
                </Card>
              </Grid>
            ))
          ) : (
            <Typography variant="h6" component="p">
              Your cart is empty.
            </Typography>
          )}
        </Grid>
      </Container>
    </div>
  );
}
