import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
} from "@mui/material";
import AppBarUser from "../components/AppbarUser";
import jwt_decode from "jwt-decode"; // Doğru import burada

export default function Home() {
  const [products, setProducts] = useState([]);
  const [userId, setUserId] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    const accessToken = localStorage.getItem("access_token");
    if (accessToken) {
      const decodedToken = jwt_decode(accessToken); // jwt_decode doğru kullanım burada
      setUserId(decodedToken.user_id);
      setToken(accessToken);
    } else {
      console.error("No token found");
    }
  }, []);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:8080/products", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        return data;
      } catch (error) {
        console.error("Error fetching products:", error);
        return [];
      }
    };

    const getProducts = async () => {
      const data = await fetchProducts();
      setProducts(data);
    };

    if (token) {
      getProducts();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [token]);

  return (
    <div>
      <AppBarUser />
      <Container maxWidth="lg" style={{ marginTop: "2rem" }}>
        <Typography variant="h3" component="h1" gutterBottom>
          Welcome to the Home Page
        </Typography>
        <Typography variant="h6" component="p" gutterBottom>
          This is a simple home page designed with Material-UI.
        </Typography>

        <Grid container spacing={4} style={{ marginTop: "2rem" }}>
          {products.map((product, index) => (
            <Grid item key={index} xs={12} sm={6} md={4}>
              <Card>
                <CardMedia
                  component="img"
                  height="140"
                  image={product.productImage}
                  alt={product.productName}
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {product.productName}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {product.productDescription}
                  </Typography>
                  <Typography variant="body1" color="text.primary">
                    Price: ${product.productPrice}
                  </Typography>
                  <Button
                    variant="contained"
                    color="secondary"
                    style={{ marginTop: "10px" }}
                    onClick={() => handleAddToCart(product.id)}
                  >
                    Add to Cart
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </div>
  );

  function handleAddToCart(productId) {
    if (!userId) {
      console.error("User ID is not available");
      return;
    }

    // Handle adding the product to the cart with userId
    fetch(`http://localhost:8080/cart/add`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({
        userId: userId,
        productId: productId,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to add product to cart");
        }
        return response.json();
      })
      .then((data) => {
        console.log("Product added to cart:", data);
      })
      .catch((error) => {
        console.error("Error adding product to cart:", error);
      });
  }
}
