import React, { createContext, useContext, useState, useEffect } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  const login = async (data) => {
    if (data && data.username && data.role) {
      // userId'yi backend'den çek
      const userId = await fetchUserId(data.username);

      // userId ile birlikte user'ı state'e kaydet
      const userData = { username: data.username, role: data.role, id: userId };
      setUser(userData);
      localStorage.setItem("user", JSON.stringify(userData));
    } else {
      console.error("Login failed: Invalid data", data);
    }
  };

  const fetchUserId = async (username) => {
    try {
      const accessToken = localStorage.getItem("access_token");
      const response = await fetch(
        `http://localhost:8080/users/username/${username}`,
        {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${accessToken}`,
          },
        }
      );

      if (response.ok) {
        const data = await response.json();
        return data.id; // userId'yi döndür
      } else {
        console.error("Failed to fetch userId");
        return null;
      }
    } catch (error) {
      console.error("Error fetching userId:", error);
      return null;
    }
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
  };

  const isAuthenticated = () => {
    return !!user;
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, isAuthenticated }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
