import * as React from "react";
import { useState } from "react";
import AppBar1 from "./Appbar";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { Container, Paper } from "@mui/material";
import Button from "@mui/material/Button";
import { useNavigate } from "react-router-dom";

const paperStyle = {
  padding: "50px 20px",
  width: 600,
  margin: "20px auto",
};

export default function TextFields() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleClick = (e) => {
    e.preventDefault();
    if (!username || !password) {
      setError("Please fill out all fields.");
      return;
    }

    const infos = { username, password };
    console.log(infos);

    fetch("http://localhost:8080/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(infos),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.token) {
          localStorage.setItem("token", data.token);
          navigate("/home");
        } else {
          setError("Login failed. Please try again."); // Başarısız giriş durumunda hata mesajı gösterir
        }
      })
      .catch((error) => {
        setError("An error occurred. Please try again."); // Hata durumunda genel bir hata mesajı gösterir
        console.error("Error:", error);
      });
  };

  return (
    <div>
      <AppBar1 />
      <Container>
        <Paper elevation={3} style={paperStyle}>
          <h1 style={{ color: "#1976d2" }}>Login</h1>
          {error && <p style={{ color: "red" }}>{error}</p>}
          <Box
            component="form"
            sx={{
              "& > :not(style)": { m: 1 },
            }}
            noValidate
            autoComplete="off"
          >
            <TextField
              id="outlined-basic"
              label="User Name"
              variant="outlined"
              fullWidth
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <TextField
              id="outlined-basic"
              label="Password"
              variant="outlined"
              fullWidth
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button variant="contained" onClick={handleClick}>
              Login
            </Button>
          </Box>
        </Paper>
      </Container>
    </div>
  );
}
